declare module "taptype"{

    export function tap(type: any, callback: (type) => any): any;

    export function tapAsync(object: any, callback: (object: any, resolve, rejected) => any): any;
    
    export function tapMerge(object1: any, object2: any, callback: (merged: any) => any): any
    
    export function tapMergeAsync(object1: any, object2: any, callback: (merged: any, resolve: (arg: any) => any, rejected: (err) => any) => any): any;
}